#include <iostream>#include <opencv2/opencv.hpp>int auto_find_roi(cv::Mat img){	try{		int numb_all;		int numb;		std::vector<int> img_numb(img.cols);				for(int y = 0; y < img.rows; y++){			numb_all = 0;			for(int x = 0; x < img.cols; x++){				numb = img.at<uchar>(y,x);				numb_all += numb;			}			img_numb[y] = numb_all;			//std::cout << img_numb[y] << std::endl;		}		//find max element		//std::vector<int>::iterator mmax = std::max_element(img_numb.begin(), img_numb.end());				//find max element index		int mindex = std::max_element(img_numb.begin(), img_numb.end()) - img_numb.begin();				return mindex;	}catch(std::exception &e){		std::cerr << "Exception: " << e.what()<< std::endl;	}	return 0;}